// Javacript Code
let elem = document.querySelectorAll('.stars span');
    var array = [];
    var a;
    var l = elem.length;
    for(var i =0; i<elem.length; i++){
        a = array.push(elem[i]);
        elem[i].addEventListener('mouseover', function(e){
            e = event.target;
            for(var j = 0; j<l; j++){
                elem[j].classList.remove('fa-star');
                elem[j].classList.add('fa-star-o');
            }
            let index =  array.indexOf(e);
            for(j = 0; j<index+1; j++){
                elem[j].classList.remove('fa-star-o');
                elem[j].classList.add('fa-star');
            }
        });
        elem[i].addEventListener('mouseout', function (e) {
            e = event.target;
            let parent = e.parentElement;
            if(parent.className === "stars"){
                for(var j = 0; j<l; j++){
                    elem[j].classList.remove('fa-star');
                    elem[j].classList.add('fa-star-o');
                }
            }else{
                let active = document.querySelector('.stars');
                let len = active.getAttribute('data-stars');
                for(var j = 0; j<l; j++){
                    elem[j].classList.remove('fa-star');
                    elem[j].classList.add('fa-star-o');
                }
                for(var k =0; k<len; k++){
                    elem[k].classList.remove('fa-star-o');
                    elem[k].classList.add('fa-star');
                }
            }
        });
        elem[i].addEventListener('click',function (e) {
            e = event.target;
            for(var j = 0; j<l; j++){
                elem[j].classList.remove('fa-star');
                elem[j].classList.add('fa-star-o');
            }
            let index =  array.indexOf(e);
            for(j = 0; j<index+1; j++){
                elem[j].classList.remove('fa-star-o');
                elem[j].classList.add('fa-star');
                let stars = document.querySelector('.stars');
                stars.classList.add('active');
                let active_stars = document.querySelectorAll('.stars .fa-star');
                let len = active_stars.length;
                stars.setAttribute('data-stars', len);
            }

        })
    }
